LEVEL    21               MAIN                DATE = 79101      12/54/45C***********************************************************************C                            -B A L D E-C                       BALLOON DESIGN PROGRAMC***********************************************************************C     THIS PROGRAM CALCULATES THE SHAPE OF A FREE BALLOON GIVEN A SET UPC     INPUT CONDITIONS. IT IS FUR FULLY TAILORED BALLOONS ONLY. WITH ORC     WITHOUT AN ENDCAP. CONVERGENCE OF THE SOLUTION IS OBTAINED BYC     MATCHING THE TOP LUAL REACTION TO THE TOP LOAD. THIS PROGRAMC     INCLUDES THE EFFECTS OF THE LUAD TAPES IN THE CALCULATIONS.C***********************************************************************C     THIS PROGRAM IS BASED ON THE PROGRAMS BALLOON AND "FLATEALL",C     AFCRL-65-92. "DETERMINATION OF THE SHAPE OF A FREE BALLOON", HYC     J. H. SMALLEY.C***********************************************************************C     DESCRIPTION OF DATA DECK:C      *  EACH SE1 UF DATA CONSISTS OF THREE CARDS LISTING THE VALUESC         IN THE READ STATEMENTS 500 AND 501C         ALL DATA ARE READ IN 10 WIDE FIELDS OF THE APPROPRIATE TYPE.C        1 "FIRST" CARD INPUTS ARE AS FOLLOWS:C             PAYLOAD IN POUNDSC             ALTITUDE OPTION: 1 = ALT IN FT, 2 = ALT IN MBC             ALTITUDE IN FEET OR MILLIBARS AS DESIREDC             FILM TYPE: 1 = POLYETHYLENE, 2 = MYLARC             FILM THICKNESS IN INCHESC             LOAD TAPE TYPE: 1 = POLYESTER, 2 = KEVLARC             TAPE LOAD RATING IN POUNDSC             NUMBER OF LOAD TAPESC        2 "SECOND" CARD INPUTS ARE AS FOLLOWS:C             TOP LOAD IN POUNDS (+) UP, (-) DOWNC             STRESS CONSTANT TAU0 (USUALLY 0.00)C             STRESS CONSTANT TAU1 (USUALLY 0.00)C             SUPERPRESSURE 10.00 FUR NATURAL SHAPE)C             PRINT INCHEMENT N (0 FOR STANDARD OUTPUT)C             NON-DIMENSIONAL GORE INCREMENT DSOC             NON-DIMENSIONAL GORE LENGTH TO CAP STARTING LOCATION CSTARTC             FILM THICKNESS INCLUDING CAP IN INCHESC        3 "THIRD" CARD INPUTS ARE AS FOLLOWS:C             OUTPUT CONTROL (KEY2=2 FUR PUNCHED DECK OF SHAPE & WEIGHTC                              KEY2=1 FOR DISK FILE OF SHAPE & WEIGHT)C             OUTPUT CONTROL (MPT 15 NUMBER OF POINTS IN LOAD-ALTITUDE)C             IDENTIFY LIFTING GAS (FOR HELIUM, 1GAS=1)C             MINIMUM RECOMMENDED PAYLOAD IN POUNDSC             MAXIMUM RECOMMENDED PAYLOAD IN POUNDSC       *  IF NO ENDCAP. MAKE SURE THAT CSTART IS GREATER THAN ANYC          ANTICIPATED GORE LENGTH.C       *  THE PROGRAM REQUIRES A LAST DATA SET WITH P = 0.0 TOC          TERMINATEC************************************************************************C      DESCRIPTION OF OUTPUT:C           FOR EACH CASE THE INPUT DATA WILL FIRST BE PRINTED, FOLLOWED)C           BY A RECORD OF THE ITERATIONS REQUIRED FOR CONVERGENCE OF THEC           SOLUTION. THIS RECORD CONSISTS OF A DISPLAY OF INITIAL ANDC           FINAL ANGLES OF THE GORE WRT THE VERTICAL AXIS OF THE BALLOON.C           DURING THE FINAL ITERATION, PERTINENT VALUES ALONG THE GURE AntC           PRINTED OUT CONTINUOUSLY, FOLLOWED BY A LISTING OF FINAL VALUES.C***************************************************************    *********C      APPROPRIATE ERROR MESSAGES ARE SUPPLIED IF THE SOLUTION DOES NOTC      CONVERGELEVEL    21                 MAIN            DATE = 78226        12/29/42****************************************************************************************************************************************************************************MAIN PROGRAM ***********************************************************************************************************     REAL K,KT,KTAPE,LAMBDA,IAM     INTEGER CODEF,CODET     DIMENSION GP (100),GW(100),GWT(100),GT(100) 100 WRITE(6,600) 600 FORMAT('1')     READ(5,500) P,KEY,CONST,CODEF,FTHICK,CODET,TLP,NT 500 FORMAT(F10.0,I10,F10.0,I10,F10.4,I10,F10.0,I10)     READ(5,501) TL,TAU0,TAU1,ALPHA,N,DSO,CSTART,CAP 501 FORMAT(F10.2,F10.2,F10.2,F10.2,110,F10.2,F10.2,F10.4)C------------------------------------------------------------------------C    IF NO MORE DATA, EXIT.     IF(P) 2CC.3CC.200C------------------------------------------------------------------------ 200 CONTINUE     READ(5,502) KEY2,MFT,IGAS,FMIN,PMAX 502 FORMAT(3110.2F10.0)     CALL DESIGN (F KEY CONST,CDDE,FTHICK,CULET TLR.NT.TL.TAUO, TAU1,     $ALPHA N,DS0,CSTART,CAP,GP,WBAL,KEY2,5)CC             CCMFUTE LCAC- ALTITUDE CURVEC     WRITE(6,600)     WRITE(6,601) 601 FORMAT(50X, 'LOAD - ALTITUDE DATA',/42X, 'GROSS AIRBORN',/37X.'    1WEIGHT - (KG)', 10X , 'ALTITUDE - (KM)')     GPMIN=1.+WBAL     GPMAX=GFMIN+ (PMAX-PAIN)/P     DG=(GFMAX-GFMIN)/NFT     GP1=GPM IN 250 CONTINUE     CALL GLNGTH(GP1.SLMDA.1)     01=P/(S/SLMDA)**3/.06243     CALL CYNCY (P1,PA,IGAS,TCHIGH)     CALL ATMOS2(PA,H,TA,RHCA,B,I AM,GXPAN,TOHIGH)     GFCS=GP1*P*.454     H=H/1000.     WRITE(6,602)GROS,H 602 FORMAT(42X,E15,7,8x,E15,7)     GP1=GP1+DG     IF(GP1.GTGPMAX)GO TO 100     GO TO 250 300 CONTINUE     STOP     ENDLEVEL    21              DESIGN                 DATE = 78226 21/29/42    SUBRCUTINE DESIGN (P.KEY.CONST.CODEF, FTHICK, CODEF, TLR.NT, TL,TAUD.   STAUI. ALPHA.N.DSC.CSTART.CAP.GP,GW.WBAL KEY2.5)******************************************************************************************************SUBROUTINE DESIGN*************************************************************************************************     REAL K,KT,KTAFE,NU     REAL LAMBDA     INTEGER CODEF, COUET     DIMENSION Y(6),G(6), G(6) E(4),C(4).D(4), WORD1(3), WORD2(3)     DIMENSION GP (100).G(100) GWT(100) GT(100)     DIMENSION SSTORE (300), GST CRE (300), WSTORE (300), TSTORE (300)     REAL F1(3)/'POLY', 'ETHY','LENE'/     REAL F2(3)/'MYLA', 'R','/     REAL T1(3)/ 'POLY' 'ESTE','R'/     REAL T2(3)/ 'KEVL' 'AR','/     REAL K1/' FT'/     REAL K2/' MB'/CC    B. C. AND U ARE CONSTANTS USED IN GILL'S MODIFIED R-K METHOD FORC    SOLVING THE GOVERNING DIFFERENTIAL EQUATIONS.     DATA B/2.0.1.0.1.3.2.0/, C/0.5.0.29289.1.7071.C.5/, 0/0.5,    $0.29289.1.7071.0.16667/C     CALL MATL(P, KEY, CONST, CUDEF, FTICK,CODEF, TLP,NT,CAP,SIGMA,CSIGMA,    $TSIGMA,KT,EN.EC.ENC,LAMBDA.TSIGME)     NU EMC/EM     OMEGA 1= SIGMA/1.8453     OMEGA 2=OMEGA 1 (CSIGMA/SIGMA)     OMEGA =OMEGA1+TSIGMA/1.8453     TCMECA = TSIGMA/6.2832CC    EMPIRICAL CURVE FITS FROM SMALLEY'S REPORT. TO GET GOOD FIRST ESTIMATC    OF THETA0. GCCD FOR SIGMA BETWEEN 0.0 AND 0.8.     THETA C=1.347     IF(OMEGA.LT.0.43354) THETAO=0.72*OMEGA+1.1015     IF(OMEGA.LT.C.35225) THETAO 1.15*OMEGA+0.9507     IF(OMEGA.LT.0.270961 THETA0=1.46*0MEGA+0.8687     OMEGA=OMEGA1C     I QUIT = 0     LSTRUN=1     IF(CODEF.EQ.2) GO TO 52     DC 51 I=1.3 51  WORDI (1)=F1 (1)     GO TO 54 52  DO 53 I=1.3 53  WORD1(1)=F2(1) 54  IF(CODEF.EQ.2) GO TO SE     DC 55 I=1.3 55  WORD2(I) = T1 (I)     GO TO 58 56  DC 57 I=1.3 57  WORD 2(1)=T2(1) 58  IF (KEY.EC.2) GO TO 59     WORD3=K1     GO TO 60 59  WORDS=K2 6O  CONTINUELEVEL 21                DESIGN            DATE = 78226   21/29/42     WRITE(6,601) (CRD1 (1) I=1.3),FTHICK.SIGMA,TAJD. (WORD2(1), 1=1,3).    SEM TAU1 TLR EC.ALPHA 601 FORMAT(5X.J7('*')/5x,;*',35x.'*',15X, 'NON-DIMENSIONAL QUANTITIES',    18x. MISC. PARAME TER S/5X.** FILM TYPE 8X.: 44.44.44.***/5X.    2'* FILM THICKNESS :'F7.4', IN',4X.'*'.17X, 'SIGMA FILM=', F9.4    3.12X. 'TAUC=', Fb.3/5x. TAPL TYPEX.:A4, A4, 44. **.17x.    4 EM SX..Fd.3.13X. TAU1 =F 6. 3/5x.'* TAPE LOA) RATING: ',    5F6.0', LOS17.EC9XF8.3.13X, ALPHA =, F6.3)     WRITE(6,602) AT EMC DSC,F,TSIGMA CSTART.TL.KT.N.CONST.WCRO3 602 FORMAT(' ',4X,'*  NUMBER OF TAPES  : '.15, 9X,'*',17X,'EMC',8x,    1'=',F8.3,13x,'DSO   =',F6.3/5x,'*  PAYLOAD',10x,': ',F6.0,'  LBS    2 *',17x, 'SIGMA TAPE =',F9.4,12x, 'CSTART=',F5.2/5X,'*  (INCL TOPLOA    3D OF): 'F6.0,'  LBS   *',17X,'KT',9X,'=',F6.1,15X,'N     =',I2/5X    4,'* DESIGN ALTITUDE :F7.0,44, *'/5x35x,'*'/5x,37('*')    5.///5X, ITERATION RECORD (INITIAL AND FINAL ANGLES)'//)C---------------------------------------------------------------------C     IF THE SOLUTION IS NOT CONVERGING. EXIT   21 IQUIT = IQUIT +1      IF(IQUIT.LT.20) GO TO 22      WRITE(0.03)  603 FORMAT(*** SOLUTICA IS NOT CONVERGING - MAXIMUM NUMBER OF IT     $ERRATIONS HAS BEEN EXCEEDED ****)      GO TO 5CC     LSTRUN = 0 IS THE KEY FOR LAST ITERATION. IT IS AN OUTPUT CONTROL.   22 IF(LSTRUN.NE.0) GO TO 23C-------------------------------------------------------------------      WRITE(6,604)  604 FORMAT(0.95X.27-MANUFACTURER'S TABLE LAYOUT//10x. S. 13x. p.     113X.2.13X.T.11X. TAUM .9XT AUC 8X, GCRE POSITION (FT)'.5X.     2' HALF GORE WIDTH (IN)/)  23  DC 24 I=1.0  24  Q(1)=0.  231 00 241 1=1,300      SSTORE(I)=0.0      WSTORE(I)=0.      TSTORE(I)=0.  241 GSTORE(1)=0.0      JCOUNT=1      DS = CSO      SSTOP CSTART      OMEGA=OMEGA1      TOMEGA = TSIGMA/6.2832      Tw=0.      ITAG=0      RC=C.      GW2=0.      S0=0.      SODIM=0.      R=0.      S=0.      Z=0.      WSUM=0.      ASUM=0.      TAUM=0.      TAUC=TAUO      THETA = THETAC      RTO=1/6.2832/COS(THETA)LEVEL 21                    DESIGN                DATE 78226 21/29/42      Y(1)=THETA      Y(2)=R      Y(3)=Z+ALPHA      Y(4)=((F+TL)/P)/6.2932/COS(THETA)      Y(5)=C.C      Y(6)=C.C      SSTORE(JCCUNT) = SJCIM      GSTORE(JCCUNT) = GW2    2 T=6.2832*Y(4)      IF (LSTHUN)34.32.34   32 WRITE(6,6c5) S.R.Z.T. TAUM, TAUC. SODIM.GW2  605 FORMAT(4(4X,F10,5),4X,FB,3.5x,F8.3,12x,7,1, 17X,F 7.2)      IF (KEY2.0.1)RITE(3.650)Y(2) Y(3) Y(4) Y(1),Y(6)      IF (KEY2 EU.2)WRITE( 7.650)Y(2) Y(2).Y(4) Y(1),Y(6)  650 FORMAT(5E15.7)C-------------------------------------------------------------------C     ICOUNT AND N ARE OUTPUT CONTROLS USED DURING THE LAST ITERATION.C     N IS THE NUMBER CF INCREMENTS THAT WILL BE SKIPPED IN THE PRINTOUT.C   34 ICOUNT=NC-------------------------------------------------------------------C     ITAG SENDS THE PROGRAM INTO ITERATIONS AND RELEASES IT WHEN THE TOP C      OF THE BALLOON HAS BEEN REACHED      IF(ITAG-1)4.3.4C--------------------------------------------------------------------    3 IF(LSTRUN.EC.C)GO TC 35      DEGO THETAC 57.2956      THETA = THETA 57.2956      DS = DSC      WRITE(6,611) DEGO.THETAD  611 FORMAT(' '.5X.F10.5.5x.F10.5)C--------------------------------------------------------------------C     CONVERGENCE TEST: TOP LOAD REACTION WITHIN 5 LB OF APPLIED TOP LOAD.      F = -6.232*Y (4)*COS(THETA) P      IF(ABS(F+TL).LE.0.5C) LSTRLN=0CC     COMPUTE CORRECTIVE TERM CN THETAOC     DELTA(THETA01/DEL TAC THETA) IS GENERALLY APPROX. = (+1/-2)C     DTHETA IS THE DESIRED VALUE OF THETA TU MATCH THE TOP LOAD REACTIONC     TO THE APPLIED TCP LCAD.      DTHETA = DELAFCUS (TL/16.2832*P*Y(4))))      DELTA  = DTHETA - THETA      CORR = -DELTA/2.      THETAO = THETAO+CORRC      GO TO 21C----------------------------------------------------------------CC     COMPUTE AND PRINT FINAL QUANTITIES   35 VOL= 3.1416*Y (D)*(LAMEDA**3)      A SUM = (ASUM+6.2832*Y(S))*(LAMBDA**2)      WSUM = (WSUM +6.2832 UMEGA Y(5))*P TH=TW*6.2832*P      WEIGHT = WSUM+TW      F= -0.2832*Y (4)*COS (THETA)*P      THETAD=57.2956* THE TA      DECO = THETA0*57.2956      JMAX = JCOUNTLEVEL 21                     DESIGN            DATE = 78220 21/29/42       PTCF-TL/F       WBAL WEIGHT/P       WRITE(6,606) LAMBDA. VOL DEGO.ASUM, THE TAD.SUM,FTW. WEIGHT   606 FORMAT(4XFINAL CUANT IT IES (LAMECA =F7.1. FT): '//5X, 'VOLUM      1E',6x,'=',E10.3,'FT3',10x,'INITIAL ANGLE',5x,=,F/.3.' DEG'/5X.      2' AREA, 8X10.3. FT2.10x, FINAL ANGLL 7X. '=',F7.3. 'DEG'      3/5X. FILM WEIGHT = F7.0. LBS'.10X,'TCF LOAD REACTION ='F7.3'.      4' LES' /5x, 'TAPE WEIGHT ='.F7.0.' lbs'/5x,'TOT. WEIGHT =',F7.0.      5'    LBS'/)       IF(KEY2 E0.1)WRITE(3.650) F.PTOP, WEAL.S.LAMBDA       IF(KEY2 EG.2)WRITE(7.650)P.PTOP, WBAL.S.LAMBDA       IF(KEY2 EG.1)WRITE(3.651)NU.KT.EM.NT       IF(KEY2.EQ.2)WRITE(7.651)NU.KT.EM.NT   651 FORMAT (3L15.7.115)CC      GENERATE TRANSFER MATRICES... 100 EVENLY SPACED. UNDEFORMEDC      GORL COORDINATESC       DUSNEW = SSTORE(JMAX)/SS.       J=1       SNEW=C.C       GP (J)=0.0       GW(J) =0.0       GWT(J)=TOMEGA       GT (J)=TOMEGA 6.2832/NT       ARITE (6.600) GP (J)+J+GW (J). J. GWT (J).J. CT (J)   100 SNEW = SNEW+D       J = J+1       DO 101 I=1.JMAX       QUAN = SNEW-SSTORE (1)       IF(ULAN.LT.0.0) GC TC 102   101 CONTINUE   102 GP(J) = SNEW       GWE(J) = USTORE (1-1)+((SNEW-SSTORE (1-1))*(GST CRL (1)-GSTORE(I-1))/      $(SSTORE (I)-SSTORE (1-1)))       GAT(J)= WSTCHE (1-1)+((SNEW-SSTORE (1-1)) * (*STORE (I)-STORE (1-1))/      $(SSTORE (I)-SSTORE (1-1)))       GT (J) TSTORE (1-1)+((SNEW-SSTORE(I-1)) * (TSTURE (I)-TSTURE ((-1))/      $(SSTORE (I)-SSTORE (1-1)))       WRITE(6,600) J.GP (J),J.G(J). J.GHT(J).J.GT(J   600 FORMAT: (' ',5X,'GP(',13,') 10.3.10x. GW,13,) = F 10.3.10x.      1'GWT('.13')=',F10.3.10x. 'GT ('13,') = '.F10.3)   652 FORMAT(4E15.7)       IF(J.EQ.100) EC TC 103       GO TO ICO   103 CONTINUE       DC 300 J=1. JMAX       IF (KEY2.EQ.1)WRITE(3.652)SSTORE(J),GSTORE(J),WSTORE(J),TSTORE(J)       IF (KEY2.EQ.2)WRITE(7.652)SSTORE(J),GSTORE(J),WSTORE(J),TSTORE(J)   300 CONTINUE  C       GO TO 5C----------------------------------------------------------------------C      COMPUTATIONAL PORTIONC----------------------------------------------------------------------CC      CHECK IF CVER THE SHOULDER OF THE BALLOON     4 IF(SIN(THETA))6.8.8LEVEL 21              DESIGN                DATE = 78226 21/29/42      IF SO CHECK IF WITHIN US OF THE TOP    6 IF(R-CS) 17.7.8      IF SO, REDEFINE DS. KEY WITH ITAG    7 US=R/ABS (SIN (THETA))      ITAG=1C--------------------------------------------------------------------C     PROGRAM SOLVING STARTS HERECC     SET UP FOR GILL'S METHOD    8 DC 10 J=1+ 4      G(2)=SIN(Y(1))      G(3)=CCS(Y(1))      G(1)=(TAUC G (3)-Y(2)*Y (3)-G(2)*Y (2)*OMEGA-TOMEGA*G(2))/Y(4)      G(4)=TAUC G(2)+G(3)*(OMEGA*Y(2)+ TOMEGA)      G(5)=Y(2)      G(6)=Y(2)*Y(2)*G(3)CC     GILL'S METHOD      DO 9 1=1.6      P1 = D(J)*(G(1)-B(J)*Q(11)      Y(I) = Y(1)+DS*F1    9 Q(1) = Q(1)+3.0*P1-C(J)*G(1)C   10 CONTINUEC------------------------------------------------------------------      TW=TW+TOMEGA DS      THETA = Y(1)      R=Y(2)      Z=Y(3)-ALPHA      S=S+CS      JCOUNT = JCOUNT +1      IF(R) 161.161.16  161 R=1.E-10   16 TAUC=TAUO+ (TAU1 (Y (4)-RTO))/      EPSM=(Y(4)/R-EMC TALC/EC)/(EM-EMC EMC/EC+KT/R/6.2832)      EPST=EPSM      TAUM=(Y (4)-KTEPST/6.2832)/R      KOER/(1+(TA UC-(ENC CPSM)/EC))      GW2=3.1416 RU LAMBD4*12./NT      SJ=SU+(CS/(1+EPSM))      SODIM=SO LAMBDA      SSTORE(JCCUNT) =SODIM      GSTORE(JCCUNT) = GW2      WSTORE(JCOUNT)=Y(2) OMEGA+ TOMEGA      TSTORE(JCCUNT)=TOMEGA 6.2832/NTC----------------------------------------------------------------------C     IF TAUC <0. BUCKLING OCCURS. SO SET VALUES TO OC      IF(TAUC) 31,17,17   31 TAUC=0.      TAU0=0.0      TAU1=0.0C---------------------------------------------------------------------C     CHECKS FOR IMPOSSIBLE BALLOONS.C     IF ITHCTAPI UR S>13.0. PRINT AN APPROPRIATE ERROR MESSAGE ANDC     PROCEED TO THE NEXT SET CF CATALEVEL 21               DESIGN                 DATE = 78226 21/29/42   17 IF( (AUS(THE TA)-3.1416).LT.0.0) GO TC 12      WRITE(6,607)  607 FORMAT('-','*** SOLUTION IS NOT CONVERGING - IMPOSSIBLE BALLOON :     $ |THETA| > PI *** ')      GO TO 5   12 IF((S-1 C. C).LT.0.0) GO TO 30      WRITE(0.608)  608 FORMAT. *** SOLUTION IS NOT CONVERGING - IMPOSSIBLE BALLOON :     $ NON-DIM S> 10.0*** *)      GO TO 5C----------------------------------------------------------------C     THIS PORTION ALLOWS FOR THE CHANGE IN FILM THICKNESS DUE TO THE CAPC   20 IF(S-SSTCF) 14.13.13   13 T=6.2832*Y(4)  131 ASUM=ASUM +6.2832*Y (5)      WSUM=SUM+6.2832 OMEGA*Y (5)      Y(5)=0.      IF(LSTRUN.NE.0) GC TC 132  609 FORMAT(0.3X. CAP STARTS AT SF6.2.      WRITE(6,009) CSTART.CSIGMA  132 OMEGA = OMEGA2      TOMEGA TS1GMB/6.2E32      SSTOP = 10.0   14 IF(ICOUNT)2.2.15   15 ICOUNT= ICOUNT-1      IF(ITAG)2.4.2    5 CONTINUE      S=S*LAMBDA      RETURN      ENDLEVEL 21                     MATL            DATE = 78226 21/29/42      SUFROUT INE MATL (P KEY CONST.CODEF.FTHICK CODEF. TLR NT.CAP. SIGMA.     SCSIGMA TSIGMA.KT.EM EC EMC LAMECA,TS IGMB)C*****************************************************************C     THIS SUBROUTINE CALCULATES MATERIAL PROPERTIES GIVEN INFORMATIONC     AS FOLLOWS:C        INPUTS: PAYLOAD, ALTITUDE OPTION KEY, ALTITUDE. FILM TYPE ANDC             THICKNESS. TAPE TYPE AND LOAD RAT ING, NUMBER OF TAPES, ANDC             CAP THICKNESSC        OUTPUTS: NON-DIMENSIONAL FILM AND TAPE WEIGHTS. TAPE STIFF-              NESS. AND FILM MCDULI.C*****************************************************************C     ALL EMPIRICAL RELATIONSHIPS USEC ARE SUBJECT TO REVISION WHENC     ADDITIONAL TEST DATA BECOME AVAILABLE.C*****************************************************************C     THIS SUBROUTINE ASSUMES. FCR LACK OF A GCCC TEMPERATURE MODEL. THATC     THE BALLOON MATERIAL 1 TEMPERATURE IS ATMOSPHERIC TEMPERATURE.C     THIS ASSUMPTION MAY NOT BE ADEQUATE.C*****************************************************************      REAL K,KT,KTAPE,LAMBDA      REAL G.NA.LF.IAN      INTEGER CODEF.CODETC----------------------------------------------------------------C     100 SERIES DEFINE NECESSARY VALUESC----------------------------------------------------------------      IF (KEY.E0.2) GC TC 101  100 H=CUNSTC      H=*0.3048C     CONVERSION OF H FROM FECT TC METERS FOR SUERT ATMOSC      CALL ATMCSCH,TA,PA,RHCA, E, IAM, GXPAN, TOHIGH)      GO TO 102  101 PA=CONST      CALL ATMCS2 (FA.H. TA RHCA.E.IAM.GXPAN.TCHIGH)C  102 B=8*6.243E-02C     CONVERSION OF 8 FROM KG/M3 TO LEN/FT3      T=TA-273.16C     CONVERSION OF TEMPERATURE FRCM DEG(R) TO DEG(C)      LAMBDA = (P/B)** (1./3.)      K = 1./((6.283185)** (1./3.1)C--------------------------------------------------------------      200 SERIES COMPUTE FILM PROPERTIESC--------------------------------------------------------------      IF(CLCEF.EQ.2) GO TO 202  201 WFILM = 58. 06 (FTHICK/12.)      EFILM = 10.02*(T**2)-1-60*(T)+41.95)*1000.      GO TO 203  202 WFILM = 87.08* (F THICK/12.)CC     EFILM = ?????    MYLAR FILM CATA IS NECESSARYC  203 SIGMA = WFILM/(K*U*LAMBDA)      CSIGMA SIGMA (CAF/FTHICK)CC     THIS PROGRAM ASSUMES A POISSON'S RATIO OF 0.50LEVEL 21               MATL               DATE = 78226  21/29/42C      EM = (EFILM*FTHICK 12.)/(H* (LAMECA** 2))      E6 = EM      EMC = EM/2.C--------------------------------------------------------------------C     300 SERIES COMPUTE TAPE PROPERTIESC--------------------------------------------------------------------      IF(CCCET.EQ.2) GC TC 302  301 WTAPE (6.1E-CE) TLR+C.Cc38+.3024*(.003+F THICK)  301 WTAPE2=TAPE+.3024* (CAF-FTHICK)      KTAPE = (TLR 10.)*(1.-(6.67E-03)*(T-20.))      GO TO 303  302 WTAPE = (2.0E-06)*TLF+0.0021      KTAPE = 26.* TLRCC     NO INFORMATION CN LCW TEMPERATURE EFFECTS CURRENTLY AVAILABLEC  303 TSIGMA = (NT*WTAPE)/ (E (LAMBDA** 2))      TSIGMB=TSIGMATAPE 2/TAPE      KT = (NT*KTAPE)/(B (LAMBDA**3))      RETURN      ENDLEVEL   21                  ATMOS            DATE = 78226   21/29/42      SUBROUTINE ATMCS (H.TA.PA.RHOA.B. IAM, GXPAN, TOHI GH)CC ******  SUBROUT INE FOR SOLVING FOR THE VALUES OF TEMPERATURE.C ******  PRESSURE CENSITY SPECIFIC HOUYANCY, INTEGRATED AIRC ******  MASS AND "GAS EXPANSION" FOR ANY GIVEN ALTITUDE BELOWC ******  61000 METERS (200131. FEET). A